contracts/facets/AdminFacet.sol.content:
// SPDX-License-Identifier: Unlicense

pragma solidity 0.8.18;

import "../storage/LibAppStorage.sol";

import {LibDiamond} from "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";

/// @notice Caller of any function in this facet must be the admin address
contract AdminFacet {
    event SetProtocolFeeBasePercentage(uint256 protocolFeeBasePercentage);
    event SetCursedBondPercentage(uint256 cursedBondPercentage);
    event WithdrawProtocolFees(uint256 totalProtocolFees, address withdrawalAddress);
    event SetGracePeriod(uint256 gracePeriod);
    event SetEmbalmerClaimWindow(uint256 embalmerClaimWindow);
    event SetExpirationThreshold(uint256 expirationThreshold);
    event AdminTransferred(address indexed previousAdmin, address indexed newAdmin);

    /// @notice Admin has attempted to set a zero value
    error CannotSetZeroValue();

    /// @notice Caller must be the admin address
    error CallerIsNotAdmin();

    /// @notice Provided address cannot be zero address
    error ZeroAddress();

    modifier onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        if (msg.sender != s.admin) {
            revert CallerIsNotAdmin();
        }
        _;
    }

    /// @notice Withdraws the total protocol fee amount from the contract to the specified address
    /// @param withdrawalAddress - the address to withdraw funds to
    function withdrawProtocolFees(address withdrawalAddress) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        // Get the total protocol fees from storage
        uint256 totalProtocolFees = s.totalProtocolFees;
        // Set the total protocol fees to 0 before the transfer to avoid reentrancy
        s.totalProtocolFees = 0;
        // Transfer the protocol fee amount to the sender after setting state
        s.sarcoToken.transfer(withdrawalAddress, totalProtocolFees);
        emit WithdrawProtocolFees(totalProtocolFees, withdrawalAddress);
    }

    /// @notice Sets the protocol fee base percentage, used to calculate protocol fees
    /// @notice The denominator is 10000
    /// @param protocolFeeBasePercentage percentage to set
    function setProtocolFeeBasePercentage(uint256 protocolFeeBasePercentage) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        s.protocolFeeBasePercentage = protocolFeeBasePercentage;
        emit SetProtocolFeeBasePercentage(protocolFeeBasePercentage);
    }

    /// @notice Sets the digging fee / cursed bond ratio
    /// @notice The denominator is 10000
    /// used to calculate how much bond archaeologists must lock per curse.
    /// @param cursedBondPercentage ratio to set.
    function setCursedBondPercentage(uint256 cursedBondPercentage) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        if (cursedBondPercentage == 0) {
            revert CannotSetZeroValue();
        }
        s.cursedBondPercentage = cursedBondPercentage;
        emit SetCursedBondPercentage(cursedBondPercentage);
    }

    /// @notice Updates the resurrection grace period
    /// @notice Denominated in seconds
    /// @param gracePeriod to set
    function setGracePeriod(uint256 gracePeriod) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        s.gracePeriod = gracePeriod;
        emit SetGracePeriod(gracePeriod);
    }

    /// @notice Updates the embalmerClaimWindow
    /// @notice Denominated in seconds
    /// @param embalmerClaimWindow to set
    function setEmbalmerClaimWindow(uint256 embalmerClaimWindow) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        s.embalmerClaimWindow = embalmerClaimWindow;
        emit SetEmbalmerClaimWindow(embalmerClaimWindow);
    }

    /// @notice Updates the expirationThreshold used during sarcophagus creation
    /// @notice Denominated in seconds
    /// @param expirationThreshold to set
    function setExpirationThreshold(uint256 expirationThreshold) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        s.expirationThreshold = expirationThreshold;
        emit SetExpirationThreshold(expirationThreshold);
    }

    /// @notice Transfers admin address to newAdmin.
    /// @param newAdmin to set
    function transferAdmin(address newAdmin) external onlyAdmin {
        AppStorage storage s = LibAppStorage.getAppStorage();
        if (newAdmin == address(0)) {
            revert ZeroAddress();
        }
        s.admin = newAdmin;
        emit AdminTransferred(msg.sender, newAdmin);
    }
}

contracts/facets/ArchaeologistFacet.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../libraries/LibTypes.sol";
import "../storage/LibAppStorage.sol";
import {LibUtils} from "../libraries/LibUtils.sol";
import {LibPrivateKeys} from "../libraries/LibPrivateKeys.sol";
import {LibErrors} from "../libraries/LibErrors.sol";
import {LibBonds} from "../libraries/LibBonds.sol";

contract ArchaeologistFacet {
    /// @notice Emitted when an archaeologist successfully publishes their private key for a sarcophagus
    /// @param sarcoId ID of sarcophagus archaeologist has published the private key on
    /// @param privateKey private key that has been published
    event PublishPrivateKey(
        bytes32 indexed sarcoId,
        bytes32 privateKey,
        address indexed archaeologist
    );

    event DepositFreeBond(address indexed archaeologist, uint256 depositedBond);

    event RegisterArchaeologist(
        address indexed archaeologist,
        string peerId,
        uint256 minimumDiggingFee,
        uint256 maximumRewrapInterval,
        uint256 freeBond,
        uint256 maximumResurrectionTime,
        uint256 curseFee
    );

    event UpdateArchaeologist(
        address indexed archaeologist,
        string peerId,
        uint256 minimumDiggingFee,
        uint256 maximumRewrapInterval,
        uint256 freeBond,
        uint256 maximumResurrectionTime,
        uint256 curseFee
    );

    event WithdrawFreeBond(address indexed archaeologist, uint256 withdrawnBond);

    event WithdrawReward(address indexed archaeologist, uint256 withdrawnReward);

    /// @notice An archaeologist that has already been successfully accused has attempted to publish their private key
    /// @param archaeologistAddress Address of accused archaeologist who is attempting to publish their private key
    /// @param sarcoId ID of sarcophagus archaeologist has attempted to publish a key on
    error ArchaeologistHasBeenAccused(address archaeologistAddress, bytes32 sarcoId);

    /// @notice Archaeologist has attempted to publish a key before the resurrection time
    /// @param currentTime Timestamp of the failed publish attempt
    /// @param resurrectionTime Time after which the sarcophagus can be resurrected
    error TooEarlyForPublish(uint256 currentTime, uint256 resurrectionTime);

    /// @notice Archaeologist has attempted to publish a key after the end of the resurrection window
    /// @param currentTime Timestamp of the failed publish attempt
    /// @param publishDeadline Time after which the sarcophagus can no longer be resurrected  (resurrectionTime + gracePeriod)
    error TooLateForPublish(uint256 currentTime, uint256 publishDeadline);

    /// @notice Archaeologist has attempted to publish a key for a sarcophagus twice
    /// @param archaeologistAddress address of publishing archaeologist
    error ArchaeologistAlreadyPublishedPrivateKey(address archaeologistAddress);

    /// @notice Archaeologist has attempted to set a zero minimumDiggingFeePerSecond or maximumRewrapInterval
    error CannotSetZeroProfileValue();

    /// @notice Archaeologist has attempted to publish the incorrect private key for a sarcophagus
    /// @param archaeologistAddress address of publishing archaeologist
    /// @param publicKey publicKey stored for archaeologist on the sarcophagus
    /// @param privateKey privateKey the archaeologist has attempted to publish
    error ArchaeologistPublishedIncorrectPrivateKey(
        address archaeologistAddress,
        bytes publicKey,
        bytes32 privateKey
    );

    /// @notice Registers the archaeologist profile
    /// @param peerId The libp2p identifier for the archaeologist
    /// @param minimumDiggingFeePerSecond The archaeologist's minimum amount to earn per second for being cursed
    /// @param maximumRewrapInterval The longest interval of time from a rewrap time the arch will accept
    /// for a resurrection
    /// @param freeBond How much bond the archaeologist wants to deposit during the register call (if any)
    /// @param maximumResurrectionTime The time beyond which the archaeologist is not willing to accept new curses or rewraps
    /// @param curseFee The fee the archaeologist sets to roughly cover the cost of a publishPrivateKey transaction
    function registerArchaeologist(
        string memory peerId,
        uint256 minimumDiggingFeePerSecond,
        uint256 maximumRewrapInterval,
        uint256 freeBond,
        uint256 maximumResurrectionTime,
        uint256 curseFee
    ) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        // verify that the archaeologist does not already exist
        LibUtils.revertIfArchProfileExists(msg.sender);

        if (maximumRewrapInterval == 0 || minimumDiggingFeePerSecond == 0) {
            revert CannotSetZeroProfileValue();
        }

        // create a new archaeologist
        LibTypes.ArchaeologistProfile memory newArch = LibTypes.ArchaeologistProfile({
            peerId: peerId,
            minimumDiggingFeePerSecond: minimumDiggingFeePerSecond,
            maximumResurrectionTime: maximumResurrectionTime,
            maximumRewrapInterval: maximumRewrapInterval,
            freeBond: freeBond,
            cursedBond: 0,
            curseFee: curseFee
        });

        // transfer SARCO tokens from the archaeologist to this contract, to be
        // used as their free bond. can be 0.
        if (freeBond != 0) {
            s.sarcoToken.transferFrom(msg.sender, address(this), freeBond);
        }

        // save the new archaeologist into relevant data structures
        s.archaeologistProfiles[msg.sender] = newArch;
        s.archaeologistProfileAddresses.push(msg.sender);

        emit RegisterArchaeologist(
            msg.sender,
            newArch.peerId,
            newArch.minimumDiggingFeePerSecond,
            newArch.maximumRewrapInterval,
            newArch.freeBond,
            newArch.maximumResurrectionTime,
            newArch.curseFee
        );
    }

    /// @notice Updates the archaeologist profile
    /// @param peerId The libp2p identifier for the archaeologist
    /// @param minimumDiggingFeePerSecond The archaeologist's minimum amount to earn per second for being cursed
    /// @param freeBond How much bond the archaeologist wants to deposit during the update call (if any)
    /// @param maximumRewrapInterval The longest interval of time from a rewrap time the arch will accept
    /// for a resurrection
    /// @param curseFee The fee the archaeologist sets to roughly cover the cost of a publishPrivateKey transaction
    function updateArchaeologist(
        string memory peerId,
        uint256 minimumDiggingFeePerSecond,
        uint256 maximumRewrapInterval,
        uint256 freeBond,
        uint256 maximumResurrectionTime,
        uint256 curseFee
    ) external {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // verify that the archaeologist exists
        LibUtils.revertIfArchProfileDoesNotExist(msg.sender);

        if (maximumRewrapInterval == 0 || minimumDiggingFeePerSecond == 0) {
            revert CannotSetZeroProfileValue();
        }

        LibTypes.ArchaeologistProfile storage existingArch = s.archaeologistProfiles[msg.sender];
        existingArch.peerId = peerId;
        existingArch.minimumDiggingFeePerSecond = minimumDiggingFeePerSecond;
        existingArch.maximumRewrapInterval = maximumRewrapInterval;
        existingArch.maximumResurrectionTime = maximumResurrectionTime;
        existingArch.curseFee = curseFee;

        // transfer SARCO tokens from the archaeologist to this contract, to be
        // used as their free bond. can be 0.
        if (freeBond != 0) {
            s.archaeologistProfiles[msg.sender].freeBond += freeBond;
            s.sarcoToken.transferFrom(msg.sender, address(this), freeBond);
        }

        emit UpdateArchaeologist(
            msg.sender,
            existingArch.peerId,
            existingArch.minimumDiggingFeePerSecond,
            existingArch.maximumRewrapInterval,
            freeBond,
            existingArch.maximumResurrectionTime,
            existingArch.curseFee
        );
    }

    /// @notice Deposits an archaeologist's free bond to the contract.
    /// @param amount The amount to deposit
    function depositFreeBond(uint256 amount) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibUtils.revertIfArchProfileDoesNotExist(msg.sender);

        // Increase the archaeologist's free bond in app storage
        s.archaeologistProfiles[msg.sender].freeBond += amount;

        // Transfer the amount of sarcoToken from the archaeologist to the contract
        s.sarcoToken.transferFrom(msg.sender, address(this), amount);
        // Emit an event
        emit DepositFreeBond(msg.sender, amount);
    }

    /// @notice Withdraws an archaeologist's free bond from the contract.
    /// @param amount The amount to withdraw
    function withdrawFreeBond(uint256 amount) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibUtils.revertIfArchProfileDoesNotExist(msg.sender);
        // Decrease the archaeologist's free bond amount.
        // Reverts if there is not enough free bond on the contract.
        s.archaeologistProfiles[msg.sender].freeBond -= amount;

        // Transfer the amount of sarcoToken to the archaeologist
        s.sarcoToken.transfer(msg.sender, amount);

        emit WithdrawFreeBond(msg.sender, amount);
    }

    /// @notice Withdraws all rewards from an archaeologist's reward pool
    function withdrawReward() external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        uint256 amountToWithdraw = s.archaeologistRewards[msg.sender];
        s.archaeologistRewards[msg.sender] = 0;

        // Transfer the amount of sarcoToken to the archaeologist
        s.sarcoToken.transfer(msg.sender, amountToWithdraw);

        emit WithdrawReward(msg.sender, amountToWithdraw);
    }

    /// @notice Publishes the private key for which the archaeologist is responsible during the
    /// sarcophagus resurrection window.
    /// Pays digging fees to the archaeologist and releases their locked bond.
    /// Cannot be called on a compromised or buried sarcophagus.
    /// @param sarcoId The identifier of the sarcophagus for which the archaeologist is responsible
    /// @param privateKey The private key the archaeologist is publishing
    function publishPrivateKey(bytes32 sarcoId, bytes32 privateKey) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // Confirm sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm sarcophagus is not buried
        if (sarcophagus.resurrectionTime == type(uint256).max) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        // Confirm current time is after resurrectionTime
        if (block.timestamp < sarcophagus.resurrectionTime) {
            revert TooEarlyForPublish(block.timestamp, sarcophagus.resurrectionTime);
        }

        // Confirm current time is within gracePeriod
        if (block.timestamp > sarcophagus.resurrectionTime + s.gracePeriod) {
            revert TooLateForPublish(block.timestamp, sarcophagus.resurrectionTime + s.gracePeriod);
        }

        // Confirm tx sender is an archaeologist on the sarcophagus
        LibTypes.CursedArchaeologist storage cursedArchaeologist = s
            .sarcophagi[sarcoId]
            .cursedArchaeologists[msg.sender];
        if (cursedArchaeologist.publicKey.length == 0) {
            revert LibErrors.ArchaeologistNotOnSarcophagus(msg.sender);
        }

        // Confirm archaeologist has not already leaked their private key
        if (cursedArchaeologist.isAccused) {
            revert ArchaeologistHasBeenAccused(msg.sender, sarcoId);
        }

        // Confirm archaeologist has not already published their private key
        if (cursedArchaeologist.privateKey != 0) {
            revert ArchaeologistAlreadyPublishedPrivateKey(msg.sender);
        }

        // Confirm that the private key being submitted matches the public key stored on the
        // sarcophagus for this archaeologist
        if (!LibPrivateKeys.isPublicKeyOfPrivateKey(privateKey, cursedArchaeologist.publicKey)) {
            revert ArchaeologistPublishedIncorrectPrivateKey(
                msg.sender,
                cursedArchaeologist.publicKey,
                privateKey
            );
        }

        // Store the private key on cursed archaeologist
        cursedArchaeologist.privateKey = privateKey;

        // Free archaeologist locked bond and transfer digging fees
        LibBonds.freeArchaeologist(sarcoId, msg.sender);

        emit PublishPrivateKey(sarcoId, privateKey, msg.sender);
    }
}

contracts/facets/EmbalmerFacet.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "../libraries/LibTypes.sol";
import "../storage/LibAppStorage.sol";
import {LibErrors} from "../libraries/LibErrors.sol";
import {LibBonds} from "../libraries/LibBonds.sol";
import {LibUtils} from "../libraries/LibUtils.sol";

contract EmbalmerFacet {
    /// @notice Emitted when a sarcophagus is created
    /// @param sarcoId Id of the new sarcophagus
    /// @param name Name of the new sarcophagus
    /// @param resurrectionTime Resurrection time of the new sarcophagus
    /// @param creationTime Creation time as set during negotiation, not the same as blocktime at which event is emitted
    /// @param embalmer Address of embalmer
    /// @param recipient Address of recipient
    /// @param cursedArchaeologists Array of addresses of cursed archaeologists
    /// @param totalDiggingFees Total digging fees charged to embalmer to create the sarcophagus
    /// @param arweaveTxId arweave tx id for the sarcophagus
    event CreateSarcophagus(
        bytes32 indexed sarcoId,
        string name,
        uint256 resurrectionTime,
        uint256 creationTime,
        address indexed embalmer,
        address indexed recipient,
        address[] cursedArchaeologists,
        uint256 totalDiggingFees,
        string arweaveTxId
    );

    /// @notice Emitted when a sarcophagus is rewrapped
    /// @param sarcoId Id of sarcophagus that was rewrapped
    /// @param resurrectionTime New resurrection time for the sarcophagus
    /// @param totalDiggingFees Total digging fees charged to the embalmer for the rewrap
    /// @param rewrapSarcophagusProtocolFees Total protocol fees charged to the embalmer for the rewrap
    event RewrapSarcophagus(
        bytes32 indexed sarcoId,
        uint256 resurrectionTime,
        uint256 totalDiggingFees,
        uint256 rewrapSarcophagusProtocolFees
    );

    /// @notice Emitted when a sarcophagus is buried
    /// @param sarcoId Id of sarcophagus that was buried
    event BurySarcophagus(bytes32 indexed sarcoId);

    /// @notice Parameters of a sarcophagus, supplied during sarcophagus creation
    struct SarcophagusParams {
        string name;
        // highest rewrap interval cursed archaeologists have agreed to accept for lifetime of sarcophagus
        uint256 maximumRewrapInterval;
        // The timestamp beyond which the sarcophagus can no longer be rewrapped
        uint256 maximumResurrectionTime;
        address recipientAddress;
        uint256 resurrectionTime;
        uint8 threshold;
        uint256 creationTime;
    }

    /// @notice Parameters of an archaeologist's curse, supplied during sarcophagus creation
    struct CurseParams {
        bytes publicKey;
        address archAddress;
        uint256 diggingFeePerSecond;
        uint256 curseFee;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with an id that is already in use
    /// @param sarcoId Id that is already in use
    error SarcophagusAlreadyExists(bytes32 sarcoId);

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with expired parameters
    /// @param currentTime Timestamp of the failed create attempt
    /// @param creationTime Time when the sarcophagus parameters were created
    /// @param creationDeadline Deadline for creation of a sarcophagus with the supplied parameters
    error SarcophagusParametersExpired(
        uint256 currentTime,
        uint256 creationTime,
        uint256 creationDeadline
    );

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with no archaeologists
    error NoArchaeologistsProvided();

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with a shamir secret sharing threshold of 0
    error ThresholdCannotBeZero();

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with more required archaeologists than total archaeologists
    error ThresholdGreaterThanTotalNumberOfArchaeologists(
        uint8 threshold,
        uint256 totalNumberOfArchaeologists
    );

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with an archaeologist list that contains the same archaeologist more than once
    error ArchaeologistListContainsDuplicate(address archaeologistAddress);

    /// @notice Emitted when an embalmer attempts to rewrap a sarcophagus with a resurrection time that has already passed
    /// @param currentTime Timestamp of the failed rewrap attempt
    /// @param resurrectionTime Resurrection timestamp which has already passed
    error ResurrectionTimeInPast(uint256 currentTime, uint256 resurrectionTime);

    /// @notice Emitted when an embalmer attempts to create a sarcophagus with a public key that has already been assigned to another sarcophagus
    /// @param publicKey the duplicated public key
    error DuplicatePublicKey(bytes publicKey);

    /// @notice Emitted when an embalmer attempts to rewrap a sarcophagus with a resurrection time that exceeds the maximum rewrap interval
    /// @param resurrectionTime Resurrection timestamp which is too far in the future
    /// @param sarcophagusMaximumRewrapInterval Maximum rewrap interval set for the sarcophagus
    /// @param maximumPermissibleResurrectionTime Resurrection timestamp which is too far in the future
    error ResurrectionTimeTooFarInFuture(
        uint256 resurrectionTime,
        uint256 sarcophagusMaximumRewrapInterval,
        uint256 maximumPermissibleResurrectionTime
    );

    /// @notice Emitted when the resurrection time defined during sarcohpagus creation or rewrap goes past the max resurrection time
    /// @param resurrectionTime The resurrection time defined during the sarcophagus creation or rewrap
    /// @param maxResurrectionTime The maximum allowed resurrection time
    error ResurrectionTimePastMaxResurrectionTime(
        uint256 resurrectionTime,
        uint256 maxResurrectionTime
    );

    error NewResurrectionTimeInPast(uint256 currentTime, uint256 newResurrectionTime);

    error NewResurrectionTimeIsZero();

    error NewResurrectionTimeTooFarInFuture(
        uint256 resurrectionTime,
        uint256 sarcophagusMaximumRewrapInterval,
        uint256 maximumPermissibleResurrectionTime
    );

    error ResurrectionTimeTooFarPastPreviousResurrectionTime(
        uint256 resurrectionTime,
        uint256 previousResurrectionTime
    );

    /// @notice Creates a sarcophagus with the supplied parameters and locks
    /// a portion of each archaeologist's freeBond equal to the digging fees calculated for the duration
    /// of the sarcophagus until its resurrection time.
    ///
    /// Verifies that each supplied archaeologist has signed off on the sarcophagus negotiation parameters:
    ///    - `publicKey` that matches the private key the archaeologist is responsible for
    ///    - `maximumRewrapInterval` to be enforced for the lifetime of the sarcophagus. No new resurrection time for future rewraps may exceed this interval from time of rewrap.
    ///    - `creationTime` of sarcophagus
    ///    - `diggingFeePerSecond` agreed to be paid to the archaeologist during the lifetime of the sarcophagus. Paid per rewrap and publishPrivateKey. Constant.
    ///    - `curseFee` agreed to be paid to the archaeologist once during the lifetime of the sarcophagus to cover cost of publishPrivateKey tx. Paid either on first rewrap or publishPrivateKey if no rewrap has occurred Constant.
    ///
    /// @param sarcoId the identifier of the sarcophagus
    /// @param sarcophagusParams params to set on sarcophagus being created
    /// @param selectedArchaeologists the archaeologists the embalmer has selected to curse
    /// @param arweaveTxId id of tx storing the sarcophagus payload on arweave
    function createSarcophagus(
        bytes32 sarcoId,
        SarcophagusParams calldata sarcophagusParams,
        CurseParams[] calldata selectedArchaeologists,
        string calldata arweaveTxId
    ) external {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Confirm that sarcophagus with supplied id doesn't already exist
        if (s.sarcophagi[sarcoId].resurrectionTime != 0) {
            revert SarcophagusAlreadyExists(sarcoId);
        }

        // Ensure too much time hasn't passed since the sarcophagus `creationTime` that has been signed
        // off by its cursed archaeologists.
        if (block.timestamp > sarcophagusParams.creationTime + s.expirationThreshold) {
            revert SarcophagusParametersExpired(
                block.timestamp,
                sarcophagusParams.creationTime,
                sarcophagusParams.creationTime + s.expirationThreshold
            );
        }

        // Confirm that resurrection time is in the future
        if (block.timestamp >= sarcophagusParams.resurrectionTime) {
            revert ResurrectionTimeInPast(block.timestamp, sarcophagusParams.resurrectionTime);
        }

        // Confirm that resurrection or rewrap will occur before the maximumRewrapInterval elapses
        if (
            block.timestamp + sarcophagusParams.maximumRewrapInterval <
            sarcophagusParams.resurrectionTime
        ) {
            revert ResurrectionTimeTooFarInFuture(
                sarcophagusParams.resurrectionTime,
                sarcophagusParams.maximumRewrapInterval,
                block.timestamp + sarcophagusParams.maximumRewrapInterval
            );
        }

        // Confirm that the resurrection time is less than the max resurrection time
        if (sarcophagusParams.resurrectionTime > sarcophagusParams.maximumResurrectionTime) {
            revert ResurrectionTimePastMaxResurrectionTime(
                sarcophagusParams.resurrectionTime,
                sarcophagusParams.maximumResurrectionTime
            );
        }

        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // track total digging fees due upon creation of sarcophagus
        uint256 totalDiggingFees;

        {
            uint256 nSelectedArchs = selectedArchaeologists.length;
            // Validate archaeologist and threshold lengths
            if (nSelectedArchs == 0) {
                revert NoArchaeologistsProvided();
            }

            if (sarcophagusParams.threshold == 0) {
                revert ThresholdCannotBeZero();
            }

            // Ensure that k <= n in the effective k-of-n shamir secret sharing scheme
            // used to distribute keyshares among archaeologists
            if (sarcophagusParams.threshold > nSelectedArchs) {
                revert ThresholdGreaterThanTotalNumberOfArchaeologists(
                    sarcophagusParams.threshold,
                    nSelectedArchs
                );
            }

            // create the sarcophagus
            sarcophagus.name = sarcophagusParams.name;
            sarcophagus.threshold = sarcophagusParams.threshold;
            sarcophagus.resurrectionTime = sarcophagusParams.resurrectionTime;
            sarcophagus.previousRewrapTime = sarcophagusParams.creationTime;
            sarcophagus.maximumRewrapInterval = sarcophagusParams.maximumRewrapInterval;
            sarcophagus.maximumResurrectionTime = sarcophagusParams.maximumResurrectionTime;
            sarcophagus.arweaveTxId = arweaveTxId;
            sarcophagus.embalmerAddress = msg.sender;
            sarcophagus.recipientAddress = sarcophagusParams.recipientAddress;
            sarcophagus.cursedArchaeologistAddresses = new address[](nSelectedArchs);
            sarcophagus.cursedBondPercentage = s.cursedBondPercentage;

            for (uint256 i; i < nSelectedArchs; ) {
                LibUtils.revertIfArchProfileDoesNotExist(selectedArchaeologists[i].archAddress);

                // Confirm archaeologist isn't already cursed on this sarcophagus (no duplicates)
                if (
                    sarcophagus
                        .cursedArchaeologists[selectedArchaeologists[i].archAddress]
                        .publicKey
                        .length != 0
                ) {
                    revert ArchaeologistListContainsDuplicate(
                        selectedArchaeologists[i].archAddress
                    );
                }

                // Confirm archaeologist is not re-using a key pair
                if (
                    s.publicKeyToArchaeologistAddress[selectedArchaeologists[i].publicKey] !=
                    address(0)
                ) {
                    revert DuplicatePublicKey(selectedArchaeologists[i].publicKey);
                }

                LibUtils.verifyArchaeologistSignature(
                    sarcophagusParams.maximumRewrapInterval,
                    sarcophagusParams.maximumResurrectionTime,
                    sarcophagusParams.creationTime,
                    selectedArchaeologists[i]
                );

                // Curse the archaeologist on this sarcophagus
                uint256 diggingFeesDue = LibBonds.curseArchaeologist(
                    sarcoId,
                    selectedArchaeologists[i],
                    i
                );

                totalDiggingFees += diggingFeesDue;

                // "Consume" this public key so it cannot be reused in the future
                s.publicKeyToArchaeologistAddress[
                    selectedArchaeologists[i].publicKey
                ] = selectedArchaeologists[i].archAddress;
                unchecked {
                    ++i;
                }
            }

            // Transfer totalDiggingFees and the protocolFees in SARCO from embalmer to this contract
            uint256 protocolFees = LibUtils.calculateProtocolFees(totalDiggingFees);
            s.totalProtocolFees += protocolFees;
            s.sarcoToken.transferFrom(
                msg.sender,
                address(this),
                totalDiggingFees + protocolFees
            );
        }
        emit CreateSarcophagus(
            sarcoId,
            sarcophagusParams.name,
            sarcophagusParams.resurrectionTime,
            sarcophagusParams.creationTime,
            msg.sender,
            sarcophagusParams.recipientAddress,
            sarcophagus.cursedArchaeologistAddresses,
            totalDiggingFees,
            arweaveTxId
        );
    }

    /// @notice Updates the resurrectionTime on a sarcophagus. Callable by the embalmer of a sarcophagus if its
    /// resurrection time has not passed, it has not been compromised by k or more accusals, and it has not been buried.
    /// @param sarcoId the identifier of the sarcophagus
    /// @param resurrectionTime the new resurrection time
    function rewrapSarcophagus(bytes32 sarcoId, uint256 resurrectionTime) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm the sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // Confirm the sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm the sarcophagus is not buried
        if (sarcophagus.resurrectionTime == type(uint256).max) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        // Confirm tx sender is embalmer
        if (sarcophagus.embalmerAddress != msg.sender) {
            revert LibErrors.SenderNotEmbalmer(msg.sender, sarcophagus.embalmerAddress);
        }

        // Confirm resurrection time has not yet passed
        if (block.timestamp >= sarcophagus.resurrectionTime) {
            revert ResurrectionTimeInPast(block.timestamp, sarcophagus.resurrectionTime);
        }

        // Confirm that new resurrection time is in future
        if (block.timestamp >= resurrectionTime) {
            revert NewResurrectionTimeInPast(block.timestamp, resurrectionTime);
        }

        // Confirm that new resurrection time doesn't exceed sarcophagus's maximumRewrapInterval
        if (block.timestamp + sarcophagus.maximumRewrapInterval < resurrectionTime) {
            revert NewResurrectionTimeTooFarInFuture(
                resurrectionTime,
                sarcophagus.maximumRewrapInterval,
                block.timestamp + sarcophagus.maximumRewrapInterval
            );
        }

        // Confirm that the new resurrection time doesn't exceed the maximumResurrectionTime
        if (sarcophagus.maximumResurrectionTime < resurrectionTime) {
            revert ResurrectionTimePastMaxResurrectionTime(
                resurrectionTime,
                sarcophagus.maximumResurrectionTime
            );
        }

        // track total digging fees to be paid by embalmer across all archaeologists on the sarcophagus
        uint256 totalDiggingFees;

        // pay digging fee to each cursed archaeologist on the sarcophagus that has not been accused
        address[] storage archaeologistAddresses = sarcophagus.cursedArchaeologistAddresses;
        uint256 cursedBondPercentage = sarcophagus.cursedBondPercentage;

        uint256 nArchAddresses = archaeologistAddresses.length;
        for (uint256 i; i < nArchAddresses; ) {
            LibTypes.CursedArchaeologist storage cursedArchaeologist = sarcophagus
                .cursedArchaeologists[archaeologistAddresses[i]];

            if (!cursedArchaeologist.isAccused) {
                // Previous digging fees calculation ignores curseFee
                // curseFee rewards and bond are handled separately if this sarcophagus has not been rewrapped yet
                uint256 prevDiggingFees = cursedArchaeologist.diggingFeePerSecond *
                    (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime);

                uint256 newDiggingFees = cursedArchaeologist.diggingFeePerSecond *
                    (resurrectionTime - block.timestamp);

                // If the new digging fees are greater than the previous digging fees, we need to
                // increase the archaeologist's cursed bond to cover the necessary cursed bond amount
                if (newDiggingFees > prevDiggingFees) {
                    uint256 cursedBondIncrease = ((newDiggingFees - prevDiggingFees) *
                        cursedBondPercentage) / 10000;

                    // If the previous cycle's rewards can't cover the cursed bond increase, revert
                    if (cursedBondIncrease > prevDiggingFees) {
                        revert ResurrectionTimeTooFarPastPreviousResurrectionTime(
                            resurrectionTime,
                            sarcophagus.resurrectionTime
                        );
                    }

                    // Increase the archaeologist's cursed bond using digging fees paid by the embalmer
                    s.archaeologistProfiles[archaeologistAddresses[i]]
                        .cursedBond += cursedBondIncrease;

                    // Rewards are now previous digging fees - difference
                    s.archaeologistRewards[archaeologistAddresses[i]] += (prevDiggingFees - cursedBondIncrease);
                } else if (newDiggingFees < prevDiggingFees) {
                    // New digging fees are less than the previous digging fees, so some of the cursed bond can be unlocked
                    uint256 cursedBondDecrease = ((prevDiggingFees - newDiggingFees) *
                        cursedBondPercentage) / 10000;

                    // Decrease archaeologist's cursed bond by the difference
                    s
                        .archaeologistProfiles[archaeologistAddresses[i]]
                        .cursedBond -= cursedBondDecrease;

                    // Increase archaeologist's free bond by the difference
                    s
                        .archaeologistProfiles[archaeologistAddresses[i]]
                        .freeBond += cursedBondDecrease;

                    // Rewards are equal to the previous digging fees
                    s.archaeologistRewards[archaeologistAddresses[i]] += prevDiggingFees;
                } else {
                    // Rewards are equal to the previous digging fees, the cursed bond can remain the same
                    s.archaeologistRewards[archaeologistAddresses[i]] += prevDiggingFees;
                }

                // Add digging fees due for the new interval
                totalDiggingFees += newDiggingFees;

                // If sarcophagus has not been rewrapped yet, pay out the curseFee and unlock the curseFee bond
                if (!sarcophagus.isRewrapped) {
                    // Pay archaeologists the curse fee to their rewards
                    s.archaeologistRewards[archaeologistAddresses[i]] += cursedArchaeologist.curseFee;

                    // Unlock the curseFee cursed bond by debiting the cursed bond and crediting free bond
                    s.archaeologistProfiles[archaeologistAddresses[i]].cursedBond -= ((cursedArchaeologist.curseFee * cursedBondPercentage) / 10000);
                    s.archaeologistProfiles[archaeologistAddresses[i]].freeBond += ((cursedArchaeologist.curseFee * cursedBondPercentage) / 10000);
                }
            }
            unchecked {
                ++i;
            }
        }

        uint256 protocolFees = LibUtils.calculateProtocolFees(totalDiggingFees);

        // Add the protocol fee to the total protocol fees in storage
        s.totalProtocolFees += protocolFees;

        // Update the sarcophagus resurrectionTime and previousRewrapTime
        sarcophagus.resurrectionTime = resurrectionTime;
        sarcophagus.previousRewrapTime = block.timestamp;

        if (!sarcophagus.isRewrapped) {
            sarcophagus.isRewrapped = true;
        }

        // Transfer the new digging fees and protocol fees from embalmer to contract
        s.sarcoToken.transferFrom(msg.sender, address(this), totalDiggingFees + protocolFees);

        emit RewrapSarcophagus(sarcoId, resurrectionTime, totalDiggingFees, protocolFees);
    }

    /// @notice Terminates a sarcophagus by setting its resurrection time to infinity and returning locked
    /// bonds to all innocent cursed archaeologists. Callable by the embalmer of a sarcophagus if its
    /// resurrection time has not passed, it has not been compromised by k or more accusals, and it has not been buried.
    /// @param sarcoId the identifier of the sarcophagus
    function burySarcophagus(bytes32 sarcoId) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm the sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // Confirm the sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm the sarcophagus is not buried
        if (sarcophagus.resurrectionTime == type(uint256).max) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        // Confirm tx sender is embalmer
        if (sarcophagus.embalmerAddress != msg.sender) {
            revert LibErrors.SenderNotEmbalmer(msg.sender, sarcophagus.embalmerAddress);
        }
        // Confirm that the current resurrection time is in the future
        if (block.timestamp >= sarcophagus.resurrectionTime) {
            revert ResurrectionTimeInPast(block.timestamp, sarcophagus.resurrectionTime);
        }

        // for each archaeologist on the sarcophagus, unlock bond and pay digging fees
        address[] storage archaeologistAddresses = sarcophagus.cursedArchaeologistAddresses;
        uint256 nArchAddresses = archaeologistAddresses.length;
        for (uint256 i; i < nArchAddresses; ) {
            LibTypes.CursedArchaeologist storage cursedArchaeologist = sarcophagus
                .cursedArchaeologists[archaeologistAddresses[i]];

            // if the archaeologist hasn't been accused transfer them their digging fees and return their locked bond
            if (!cursedArchaeologist.isAccused) {
                LibBonds.freeArchaeologist(sarcoId, archaeologistAddresses[i]);
            }
            unchecked {
                ++i;
            }
        }

        // Set resurrection time to infinity
        sarcophagus.resurrectionTime = type(uint256).max;

        emit BurySarcophagus(sarcoId);
    }
}

contracts/facets/ThirdPartyFacet.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import {LibDiamond} from "hardhat-deploy/solc_0.8/diamond/libraries/LibDiamond.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";
import {LibBonds} from "../libraries/LibBonds.sol";
import {LibUtils} from "../libraries/LibUtils.sol";
import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";

contract ThirdPartyFacet {
    event AccuseArchaeologist(
        bytes32 indexed sarcoId,
        address indexed accuser,
        uint256 totalSlashedBondDistributed,
        uint256 totalDiggingFeesDistributed,
        address[] accusedArchAddresses
    );

    event Clean(bytes32 indexed sarcoId, address indexed cleaner);

    /// @notice Clean has been called on a sarcophagus that has already been cleaned
    /// @param sarcoId ID of sarcophagus archaeologist has attempted to publish a private key on
    error SarcophagusAlreadyCleaned(bytes32 sarcoId);

    /// @notice Clean has been called before the deadline for archaeologists to publish private keys has passed
    /// @param currentTime Timestamp of the failed clean attempt
    /// @param publishDeadline Latest time an archaeologist may publish a private key on a sarcophagus: resurrectionTime + gracePeriod
    error TooEarlyForClean(uint256 currentTime, uint256 publishDeadline);

    /// @notice Clean has been called by someone other than the admin or embalmer of the sarcophagus
    /// @param senderAddress Address of sender
    error SenderNotEmbalmerOrAdmin(address senderAddress);

    /// @notice Embalmer has attempted to clean a sarcophagus after the embalmerClaimWindow has passed
    /// @param currentTime Timestamp of the failed clean attempt
    /// @param embalmerClaimWindowEnd Latest time an embalmer may claim residual locked bonds the sarcophagus: resurrectionTime + gracePeriod + embalmerClaimWindow
    error EmbalmerClaimWindowPassed(uint256 currentTime, uint256 embalmerClaimWindowEnd);

    /// @notice Admin has attempted to clean a sarcophagus before the embalmerClaimWindow has passed
    /// @param currentTime Timestamp of the failed clean attempt
    /// @param embalmerClaimWindowEnd Latest time an embalmer may claim residual locked bonds the sarcophagus: resurrectionTime + gracePeriod + embalmerClaimWindow
    error TooEarlyForAdminClean(uint256 currentTime, uint256 embalmerClaimWindowEnd);

    /// @notice Emitted when a third party attempts to accuse an archaeologist on a sarcophagus where the resurrection time has already passed
    /// @param currentTime Timestamp of the failed accuse attempt
    /// @param resurrectionTime Resurrection timestamp which has already passed
    error ResurrectionTimeInPast(uint256 currentTime, uint256 resurrectionTime);

    /// @notice Emitted when accuse is called with an unequal numbers of public keys and signatures
    /// @param signatures the number of signatures passed into the accuse call
    /// @param publicKeys the number of public keys passed into the accuse call
    error DifferentNumberOfSignaturesAndPublicKeys(uint256 signatures, uint256 publicKeys);

    /// @notice Emitted when accuse is called with an invalid signature for the supplied sarcoId, paymentAddress, and publicKey
    /// @param sarcoId that should have been signed
    /// @param paymentAddress payment address that should have been signed
    /// @param publicKey publicKey that should be derived from signing key
    /// @param signature invalid signature
    error InvalidAccusalSignature(
        bytes32 sarcoId,
        address paymentAddress,
        bytes publicKey,
        LibTypes.Signature signature
    );

    /// @notice If archaeologists fail to publish their private keys on a sarcophagus before the end of the gracePeriod,
    /// their locked bonds and diggingFees may be claimed by either the embalmer or the admin
    /// embalmers may claim during a limited embalmerClaimWindow after the end of the gracePeriod, after that only the admin will
    /// be able to claim remaining locked bond and diggingFees
    /// @param sarcoId The identifier of the sarcophagus to clean
    function clean(bytes32 sarcoId) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm the sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // Confirm tx sender is embalmer or admin
        if (msg.sender != sarcophagus.embalmerAddress && msg.sender != LibDiamond.contractOwner()) {
            revert SenderNotEmbalmerOrAdmin(msg.sender);
        }

        // Confirm the sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm the sarcophagus is not buried
        if (sarcophagus.resurrectionTime == type(uint256).max) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        // Confirm the sarcophagus has not already been cleaned
        if (sarcophagus.isCleaned) {
            revert SarcophagusAlreadyCleaned(sarcoId);
        }

        // Confirm that the resurrectionTime + gracePeriod have passed
        if (block.timestamp <= sarcophagus.resurrectionTime + s.gracePeriod) {
            revert TooEarlyForClean(block.timestamp, sarcophagus.resurrectionTime + s.gracePeriod);
        }

        // if sender is embalmer, confirm current time is within embalmerClaimWindow
        if (
            msg.sender == sarcophagus.embalmerAddress &&
            block.timestamp > sarcophagus.resurrectionTime + s.gracePeriod + s.embalmerClaimWindow
        ) {
            revert EmbalmerClaimWindowPassed(
                block.timestamp,
                sarcophagus.resurrectionTime + s.gracePeriod + s.embalmerClaimWindow
            );
        }

        // if sender is admin, confirm embalmerClaimWindow has passed
        if (
            msg.sender == LibDiamond.contractOwner() &&
            block.timestamp <= sarcophagus.resurrectionTime + s.gracePeriod + s.embalmerClaimWindow
        ) {
            revert TooEarlyForAdminClean(
                block.timestamp,
                sarcophagus.resurrectionTime + s.gracePeriod + s.embalmerClaimWindow
            );
        }

        // sum of locked bonds and digging fees for all archaeologists that have failed to publish private keys before publish deadline and have not been accused
        uint256 totalDiggingFeesAndLockedBonds;
        uint256 nCursedArchs = sarcophagus.cursedArchaeologistAddresses.length;

        for (uint256 i; i < nCursedArchs; ) {
            LibTypes.CursedArchaeologist storage cursedArchaeologist = sarcophagus
                .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]];

            // Punish archaeologists that failed to publish their private key in time
            if (!cursedArchaeologist.isAccused && cursedArchaeologist.privateKey == 0) {
                uint256 diggingFeesDue = cursedArchaeologist.diggingFeePerSecond *
                    (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime);

                if (!sarcophagus.isRewrapped) {
                    diggingFeesDue += cursedArchaeologist.curseFee;
                }

                uint256 cursedBondDue = (diggingFeesDue * sarcophagus.cursedBondPercentage) / 10000;
                totalDiggingFeesAndLockedBonds += diggingFeesDue + cursedBondDue;

                // slash the archaeologist's locked bond for the sarcophagus
                s.archaeologistProfiles[sarcophagus.cursedArchaeologistAddresses[i]].cursedBond -= cursedBondDue;
            }
            unchecked {
                ++i;
            }
        }

        // Transfer total slashed locked bonds plus digging fees to the embalmer if they are the caller, otherwise add
        // this to the contract's protocol fees
        if (msg.sender == sarcophagus.embalmerAddress) {
            s.sarcoToken.transfer(sarcophagus.embalmerAddress, totalDiggingFeesAndLockedBonds);
        } else {
            s.totalProtocolFees += totalDiggingFeesAndLockedBonds;
        }

        sarcophagus.isCleaned = true;
        emit Clean(sarcoId, msg.sender);
    }

    /**
     * @notice Accuse one or more archaeologists of leaking private keys by submitting signatures on the sarco id
     * and payment address generated with the leaked private keys
     * If the archaeologists responsible for those private keys haven't already been accused, their locked bond will be
     * split between the embalmer and the supplied payment address and digging fees allocated for those archaeologists will be refunded to the embalmer
     *
     * If k or more archaeologists are accused over the lifetime of a sarcophagus, the sarcophagus
     * state will be updated to Accused and bonds for all remaining unaccused archaeologists will be
     * returned
     *
     * @param sarcoId The identifier of the sarcophagus having leaked private keys
     * @param publicKeys an array of public keys corresponding to leaked private keys - order must match order of signatures
     * @param signatures an array of signatures of the sarcoId and payment address signed by the leaked private keys - order must match order of publicKeys
     * @param paymentAddress the address to which rewards should be sent if successful
     */
    function accuse(
        bytes32 sarcoId,
        bytes[] calldata publicKeys,
        LibTypes.Signature[] calldata signatures,
        address paymentAddress
    ) external {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        // verify that current time is not past resurrection time
        if (block.timestamp > sarcophagus.resurrectionTime) {
            revert ResurrectionTimeInPast(block.timestamp, sarcophagus.resurrectionTime);
        }

        // Confirm the sarcophagus has not been compromised
        if (sarcophagus.isCompromised) {
            revert LibErrors.SarcophagusCompromised(sarcoId);
        }

        // Confirm the sarcophagus is not buried
        if (sarcophagus.resurrectionTime == type(uint256).max) {
            revert LibErrors.SarcophagusInactive(sarcoId);
        }

        uint256 nSigs = signatures.length;

        if (nSigs != publicKeys.length) {
            revert DifferentNumberOfSignaturesAndPublicKeys(nSigs, publicKeys.length);
        }

        address[] memory accusedArchAddresses = new address[](nSigs);

        // track the combined locked bond across all archaeologists being accused in this call
        uint256 totalCursedBond;
        uint256 accusalCount;
        for (uint256 i; i < nSigs; ) {
            if (
                !LibUtils.verifyAccusalSignature(
                    sarcoId,
                    paymentAddress,
                    publicKeys[i],
                    signatures[i]
                )
            ) {
                revert InvalidAccusalSignature(
                    sarcoId,
                    paymentAddress,
                    publicKeys[i],
                    signatures[i]
                );
            }

            // look up the archaeologist responsible for the publicKey
            address accusedArchaeologistAddress = s.publicKeyToArchaeologistAddress[publicKeys[i]];
            LibTypes.CursedArchaeologist storage accusedArchaeologist = sarcophagus
                .cursedArchaeologists[accusedArchaeologistAddress];

            // verify the accused archaeologist is cursed on the sarcophagus
            if (accusedArchaeologist.publicKey.length == 0) {
                revert LibErrors.ArchaeologistNotOnSarcophagus(msg.sender);
            }

            // if the archaeologist has already been accused on this sarcophagus skip them without taking action
            if (accusedArchaeologist.isAccused) {
                unchecked {
                    ++i;
                }
                continue;
            }

            // mark the archaeologist on the sarcophagus as having been accused
            accusedArchaeologist.isAccused = true;
            accusedArchAddresses[accusalCount++] = accusedArchaeologistAddress;

            uint256 cursedBondDue = ((accusedArchaeologist.diggingFeePerSecond *
                (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime)) *
                sarcophagus.cursedBondPercentage) / 10000;

            // If the sarcophagus has not been rewrapped, also slash the curse fee
            if (!sarcophagus.isRewrapped) {
                cursedBondDue += accusedArchaeologist.curseFee * sarcophagus.cursedBondPercentage / 10000;
            }

            totalCursedBond += cursedBondDue;

            // Slash the offending archaeologists bond
            s.archaeologistProfiles[accusedArchaeologistAddress].cursedBond -= cursedBondDue;
            unchecked {
                ++i;
            }
        }

        // if none of the accusals were valid because the archaeologists have all already been accused, return without taking action
        if (accusalCount == 0) {
            return;
        }

        {
            uint256 nCursedArchs = sarcophagus.cursedArchaeologistAddresses.length;

            // the sarcophagus is compromised if the current call has successfully accused the sss threshold of archaeologists
            if (accusalCount >= sarcophagus.threshold) {
                sarcophagus.isCompromised = true;
            } else {
                // if the current call hasn't resulted in at least sss threshold archaeologists being accused
                // check if total number of historical accusals on sarcophagus is greater than threshold
                uint256 totalAccusals;

                for (uint256 i; i < nCursedArchs; ) {
                    if (
                        sarcophagus
                            .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                            .isAccused
                    ) {
                        ++totalAccusals;
                    }
                    unchecked {
                        ++i;
                    }
                }
                // the sarcophagus is compromised if k or more archaeologists have been accused over the lifetime of the sarcophagus
                if (totalAccusals >= sarcophagus.threshold) {
                    sarcophagus.isCompromised = true;
                }
            }

            // if k or more archaeologists have been accused over the lifetime of the sarcophagus, funds should
            // be returned to the remaining well behaved archaeologists
            if (sarcophagus.isCompromised) {
                // iterate through all archaeologist addresses on the sarcophagus
                for (uint256 i; i < nCursedArchs; ) {
                    // if the archaeologist has never been accused, release their locked bond back to them
                    if (
                        !sarcophagus
                            .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                            .isAccused
                    ) {
                        LibBonds.freeArchaeologist(
                            sarcoId,
                            sarcophagus.cursedArchaeologistAddresses[i]
                        );
                    }
                    unchecked {
                        ++i;
                    }
                }
            }
        }

        uint256 halfTotalCursedBond = totalCursedBond >> 1;
        uint256 totalDiggingFees = totalCursedBond / (sarcophagus.cursedBondPercentage / 10000);
        // transfer the cursed half, plus the current digging fees, to the embalmer
        s.sarcoToken.transfer(
            sarcophagus.embalmerAddress,
            totalDiggingFees + halfTotalCursedBond
        );

        // transfer the other half of the cursed bond to the transaction caller
        s.sarcoToken.transfer(paymentAddress, halfTotalCursedBond);

        emit AccuseArchaeologist(
            sarcoId,
            msg.sender,
            totalCursedBond,
            totalDiggingFees,
            accusedArchAddresses
        );
    }
}

contracts/facets/ViewStateFacet.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "../libraries/LibTypes.sol";
import "../libraries/LibUtils.sol";
import "../storage/LibAppStorage.sol";

contract ViewStateFacet {
    /// @notice Get the admin address from diamond storage
    /// @return The admin address
    function getAdmin() external view returns (address) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.admin;
    }

    /// @notice Gets the total protocol fees from diamond storage
    /// @return The total protocol fees
    function getTotalProtocolFees() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.totalProtocolFees;
    }

    /// @notice Get the protocol fee base percentage from diamond storage
    /// @return The protocol fee base percentage - protocolFeeBasePercentage
    function getProtocolFeeBasePercentage() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.protocolFeeBasePercentage;
    }

    /// @notice Get the cursed bond percentage from diamond storage
    /// @return The cursed bond percentage - cursedBondPercentage
    function getCursedBondPercentage() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.cursedBondPercentage;
    }

    /// @notice Gets archaeologist profiles given a list of archaeologist addresses.
    /// If an invalid address is included, simply leaves it out of the list.
    /// @param addresses The list of archaeologist addresses
    /// @return The list of archaeologist profiles
    function getArchaeologistProfiles(
        address[] memory addresses
    ) external view returns (LibTypes.ArchaeologistProfile[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        uint256 nAddresses = addresses.length;
        LibTypes.ArchaeologistProfile[] memory profiles = new LibTypes.ArchaeologistProfile[](
            nAddresses
        );

        for (uint256 i; i < nAddresses; ) {
            // Skip unregistered archaeologists
            if (s.archaeologistProfiles[addresses[i]].maximumRewrapInterval == 0) {
                continue;
            }
            profiles[i] = s.archaeologistProfiles[addresses[i]];
            unchecked {
                ++i;
            }
        }

        return profiles;
    }

    /// @notice Gets the grace period an archaeologist is given to resurrect a sarcophagus after the resurrection time passes
    /// @return The resurrection grace period
    function getGracePeriod() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.gracePeriod;
    }

    /// @notice Gets the window after end of gracePeriod + resurrectionTime where embalmer can claim remaining bonds from archaeologists that have failed to publish private keys
    /// @return The embalmer claim window
    function getEmbalmerClaimWindow() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.embalmerClaimWindow;
    }

    /// @notice Gets the expiration threshold after which a sarcophagus must be renegotiated
    /// @return The expiration threshold
    function getExpirationThreshold() external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.expirationThreshold;
    }

    /// @notice Given an archaeologist address, return that archaeologist's
    /// profile
    /// @param archaeologist The archaeologist account's address
    /// @return the Archaeologist object
    function getArchaeologistProfile(
        address archaeologist
    ) external view returns (LibTypes.ArchaeologistProfile memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibUtils.revertIfArchProfileDoesNotExist(archaeologist);
        return s.archaeologistProfiles[archaeologist];
    }

    /// @notice Return the list of registereed archaeologist addresses.
    /// @return addresses of registered archaeologists
    function getArchaeologistProfileAddresses() external view returns (address[] memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfileAddresses;
    }

    /// @notice Given an index (of the full archaeologist array), return the
    /// archaeologist address at that index
    /// @param index The index of the registered archaeologist
    /// @return address of the archaeologist
    function getArchaeologistProfileAddressAtIndex(uint256 index) external view returns (address) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfileAddresses[index];
    }

    /// @notice Returns the amount of free bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being returned
    function getFreeBond(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfiles[archaeologist].freeBond;
    }

    /// @notice Returns the amount of rewards stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// reward is being returned
    function getRewards(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistRewards[archaeologist];
    }

    /// @notice Returns the amount of cursed bond stored in the contract for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being returned
    function getCursedBond(address archaeologist) external view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.archaeologistProfiles[archaeologist].cursedBond;
    }

    struct SarcophagusResponse {
        uint256 resurrectionTime;
        uint256 previousRewrapTime;
        bool isCompromised;
        bool isCleaned;
        string name;
        uint8 threshold;
        uint256 maximumRewrapInterval;
        uint256 maximumResurrectionTime;
        uint256 cursedBondPercentage;
        string arweaveTxId;
        address embalmerAddress;
        address recipientAddress;
        address[] archaeologistAddresses;
        uint8 publishedPrivateKeyCount;
        bool hasLockedBond;
    }

    /// @notice Returns data on the sarcophagus with the supplied id
    /// includes aggregate data on cursed archaeologists associated with the sarcophagus
    ///     - publishedPrivateKeyCount - the total number of private keys published by archaeologists on the sarcophagus
    ///     - hasLockedBond - true if archaeologists still have bond locked in the contract for this sarcophagus
    /// @param sarcoId The identifier of the sarcophagus being returned
    function getSarcophagus(bytes32 sarcoId) external view returns (SarcophagusResponse memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        // Confirm sarcophagus exists
        if (sarcophagus.resurrectionTime == 0) {
            revert LibErrors.SarcophagusDoesNotExist(sarcoId);
        }

        uint8 publishedPrivateKeyCount;
        bool hasLockedBond;
        uint256 archsLength = sarcophagus.cursedArchaeologistAddresses.length;
        for (uint256 i; i < archsLength; ) {
            // archaeologist has published a private key
            if (
                sarcophagus
                    .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                    .privateKey != 0
            ) {
                ++publishedPrivateKeyCount;
            } else if (
                !sarcophagus
                    .cursedArchaeologists[sarcophagus.cursedArchaeologistAddresses[i]]
                    .isAccused &&
                !sarcophagus.isCompromised &&
                !sarcophagus.isCleaned &&
                sarcophagus.resurrectionTime != type(uint256).max
            ) {
                // if the sarcophagus is not compromised, buried, or cleaned and
                // one or more unaccused archaeologists hasn't published a private key there is locked bond on the sarcophagus
                hasLockedBond = true;
            }
            unchecked {
                ++i;
            }
        }

        return
            SarcophagusResponse({
                resurrectionTime: sarcophagus.resurrectionTime,
                previousRewrapTime: sarcophagus.previousRewrapTime,
                isCompromised: sarcophagus.isCompromised,
                isCleaned: sarcophagus.isCleaned,
                name: sarcophagus.name,
                threshold: sarcophagus.threshold,
                maximumRewrapInterval: sarcophagus.maximumRewrapInterval,
                maximumResurrectionTime: sarcophagus.maximumResurrectionTime,
                cursedBondPercentage: sarcophagus.cursedBondPercentage,
                arweaveTxId: sarcophagus.arweaveTxId,
                embalmerAddress: sarcophagus.embalmerAddress,
                recipientAddress: sarcophagus.recipientAddress,
                archaeologistAddresses: sarcophagus.cursedArchaeologistAddresses,
                publishedPrivateKeyCount: publishedPrivateKeyCount,
                hasLockedBond: hasLockedBond
            });
    }

    /// @notice Returns the data stored on a sarcophagus for an archaeologist.
    /// @param sarcoId The identifier of the sarcophagus whose data is being
    /// returned
    /// @param archaeologist The address of the archaeologist whose data is
    /// being returned
    function getSarcophagusArchaeologist(
        bytes32 sarcoId,
        address archaeologist
    ) external view returns (LibTypes.CursedArchaeologist memory) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        return s.sarcophagi[sarcoId].cursedArchaeologists[archaeologist];
    }
}

contracts/libraries/LibBonds.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity 0.8.18;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "./LibErrors.sol";

import "../facets/EmbalmerFacet.sol";

library LibBonds {
    /// @notice Bonds the archaeologist to a sarcophagus.
    /// This does the following:
    ///   - adds the archaeologist's curse params and address to the sarcophagus
    ///   - calculates digging fees to be locked and later paid to archaeologist (includes curseFee)
    ///   - locks this amount from archaeologist's free bond; increases cursedBond by same
    ///   - Adds the sarcophagus' id to the archaeologist's record of bonded sarcophagi
    /// @param sarcoId Id of the sarcophagus with which to curse the archaeologist
    /// @param archaeologist The archaeologist to curse, with associated parameters of the curse
    ///
    /// @return the amount of digging fees due the embalmer for this curse
    function curseArchaeologist(
        bytes32 sarcoId,
        EmbalmerFacet.CurseParams calldata archaeologist,
        uint256 index
    ) internal returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        sarcophagus.cursedArchaeologists[archaeologist.archAddress] = LibTypes.CursedArchaeologist({
            publicKey: archaeologist.publicKey,
            privateKey: 0,
            isAccused: false,
            diggingFeePerSecond: archaeologist.diggingFeePerSecond,
            curseFee: archaeologist.curseFee
        });
        sarcophagus.cursedArchaeologistAddresses[index] = archaeologist.archAddress;

        // Calculate digging fees due for this time period (creationTime/previousRewrapTime -> resurrectionTime)
        uint256 diggingFeesDue = (archaeologist.diggingFeePerSecond *
            (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime))
            + archaeologist.curseFee;

        // Use cursed bond percentage to determine how much bond to lock up
        uint256 bondToCurse = (((diggingFeesDue) * s.cursedBondPercentage) / 10000);

        // Transfer bond to curse from free bond to cursed bond
        s.archaeologistProfiles[archaeologist.archAddress].freeBond -= bondToCurse;
        s.archaeologistProfiles[archaeologist.archAddress].cursedBond += bondToCurse;

        return diggingFeesDue;
    }

    /// @notice Calculates and unlocks an archaeologist's cursed bond. Pays due digging fees to the archaeologist.
    /// @param sarcoId the identifier of the sarcophagus to free the archaeologist from
    /// @param archaeologistAddress the address of the archaeologist to free
    function freeArchaeologist(bytes32 sarcoId, address archaeologistAddress) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();
        LibTypes.Sarcophagus storage sarcophagus = s.sarcophagi[sarcoId];

        LibTypes.CursedArchaeologist storage cursedArchaeologist = s
            .sarcophagi[sarcoId]
            .cursedArchaeologists[archaeologistAddress];

        // Calculate the digging fees to be paid since the last rewrap (or creation)
        uint256 diggingFeeAmount = cursedArchaeologist.diggingFeePerSecond *
            (sarcophagus.resurrectionTime - sarcophagus.previousRewrapTime);

        // If sarcophagus has not be been rewrapped yet, pay out the curseFee
        if (!sarcophagus.isRewrapped) {
            diggingFeeAmount += cursedArchaeologist.curseFee;
        }

        uint256 cursedBondAmount = (diggingFeeAmount * sarcophagus.cursedBondPercentage) / 10000;

        s.archaeologistProfiles[archaeologistAddress].cursedBond -= cursedBondAmount;
        s.archaeologistProfiles[archaeologistAddress].freeBond += cursedBondAmount;

        s.archaeologistRewards[archaeologistAddress] += diggingFeeAmount;
    }
}

